function wt_select_batchfile
% WT_SELECT_BATCH_FILE
% Load a list of files specified in a selected text file. Display list in
% File -> Movies. Useful for selecting many movies for batch processing.
% File lists (called 'Selections') can be automatically generated by
% selecting a top-level directory from Files -> Create Selection.
%
% The list of files should be specified in a text file with one movie per
% line. Examples:
%
%   Windows:
%       g:\video\113\11-2-2003\1\c_movie1.avi
%       g:\video\113\11-2-2003\1\c_movie2.avi
%
%   Linux/Unix:
%       /media/disk/BS/movie1.avi
%       /media/disk/BS/movie1.avi
%
%
% See also:
%   wt_create_batchfile
%

global g_tWT

% Request user for location of batchfile
[sFilename, sFilepath] = uigetfile('*', 'Select batchfile');
sBatchfile = wt_check_path(sprintf('%s%s', sFilepath, sFilename));

% Return immeditaley if user cancels
if ~sFilename return; end

% Load batchfile entries
[sPaths vTrialStart vTrialEnd] = textread(sBatchfile, '%s %d:%d');

% Check the validity of all filenames
% If valid, save in struct we will pass to the whisker tracker
g_tWT.Movies = struct([]);
for p = 1:length(sPaths)
    for t = vTrialStart(p):vTrialEnd(p)
        sFileName = wt_check_path(sprintf('%s%d', char(sPaths(p)), t));
        try
            aviinfo(sFileName);
            g_tWT.Movies(size(g_tWT.Movies,2)+1).filename = sFileName;
        end
    end
end

% Reinitialize the gui
wt_prep_gui;

% Load movie and display first frame
wt_load_movie(1);

return


